mapper.sql:
from code cc 
WHERE 1=1

AND cc.ifccDelNy = #{shDelNy}




	AND cc.ifccSeq = #{shValue}
	AND cc.ifccName LIKE CONCAT('%', #{shValue},'%')
	AND cc.ifccNameEng LIKE CONCAT('%', #{shValue},'%')



	AND cc.ifccCreatedAt BETWEEN #{shDateStart} AND #{shDateEnd}
	AND cc.ifccModifiedAt BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.insert:
INSERT INTO code(
	,ifccName
	,ifccNameEng
	,ifccUseNy
	,ifccOrder
	,ifccExplanation
	,ifccDelNy
	,ifccCreatedAt
)
VALUES (
	,#{ifccName}
	,#{ifccNameEng}
	,1
	,#{ifccOrder}
	,#{ifccExplanation}
	,0
	,now()
)

	SELECT last_insert_id()

mapper.update:
UPDATE code
SET
	ifccName = #{ifccName}
	,ifccNameEng = #{ifccNameEng}
	,ifccUseNy = #{ifccUseNy}
	,ifccOrder = #{ifccOrder}
	,ifccExplanation = #{ifccExplanation}
	,ifccDelNy = #{ifccDelNy}
	,ifccModifiedAt = now()
WHERE
	ifccSeq = #{ifccSeq}
