mapper.select:
SELECT c.ifcmSeq ,c.ifcmComment ,c.ifcmCreatedAt ,c.ifcmGrade ,s.ifstName ,s.ifstAddress ,m.ifmmName FROM comment c LEFT JOIN member m on m.ifmmSeq = c.ifmmSeq LEFT JOIN store s on s.ifstSeq = c.ifstSeq WHERE 1=1 AND ifcmDelNy = #{shDelNy} AND ifcmCreatedAt between #{shDateStart} and #{shDateEnd}
AND ifcmComment LIKE CONCAT('%',#{shValue},'%') AND ifcmGrade = #{shValue} AND ifmmName LIKE CONCAT('%',#{shValue},'%') AND ifstName LIKE CONCAT('%',#{shValue},'%')
ORDER BY c.ifcmSeq DESC

SELECT COUNT(*) from comment c LEFT JOIN member m on m.ifmmSeq = c.ifmmSeq LEFT JOIN store s on s.ifstSeq = c.ifstSeq where 1=1 AND ifcmDelNy = #{shDelNy} AND ifcmCreatedAt between #{shDateStart} and #{shDateEnd}
AND ifcmComment LIKE CONCAT('%',#{shValue},'%') AND ifcmGrade = #{shValue} AND ifmmName LIKE CONCAT('%',#{shValue},'%') AND ifstName LIKE CONCAT('%',#{shValue},'%')
ORDER BY c.ifcmSeq DESC

mapper.insert:
INSERT INTO comment (
	c.ifcmSeq
	,c.ifcmComment
)
VALUES (
	#{ifcmSeq}
	,#{ifcmComment}
)


	SELECT last_insert_id()

mapper.update:
 	UPDATE comment
 	SET
		c.ifcmComment = #{ifcmComment}
	WHERE
		c.ifcmSeq = #{ifcmSeq}
