mapper.select:
SELECT c.ifcmSeq ,c.ifcmComment ,c.ifcmCreatedAt ,c.ifcmModifiedAt ,c.ifcmDelNy ,c.ifcmGrade ,s.ifstName ,s.ifstAddress ,m.ifmmName FROM comment c LEFT JOIN store s on s.ifstSeq = c.ifstSeq LEFT JOIN member m on m.ifmmSeq = c.ifmmSeq WHERE 1=1 AND ifcmDelNy = #{shDelNy} AND ifcmCreatedAt between #{shDateStart} and #{shDateEnd}
AND ifcmComment LIKE CONCAT('%',#{shValue},'%') AND ifcmGrade = #{shValue} AND ifmmifmmName LIKE CONCAT('%',#{shValue},'%') AND ifstName LIKE CONCAT('%',#{shValue},'%')
ORDER BY c.ifcmSeq DESC

SELECT COUNT(*)
from comment c
where 1=1
AND ifcmDelNy = #{shDelNy}
AND ifcmCreatedAt between #{shDateStart} and #{shDateEnd}

	AND ifcmComment LIKE CONCAT('%',#{shValue},'%')
	AND ifcmGrade = #{shValue}
	AND ifmmifmmName LIKE CONCAT('%',#{shValue},'%')
	AND ifstName LIKE CONCAT('%',#{shValue},'%')

ORDER BY c.ifcmSeq DESC
