	,ifmmModifiedAt
	,ifmmDelNy
)
VALUES (
	#{ifmmSeq}
	,#{ifmmId}
	,#{ifmmName}
	,#{ifmmPwd}
	,#{ifmmDob}
	,#{ifmmGender}
	,#{ifmmEmail}
	,#{ifmmRadioOperator}
	,#{ifmmPhone}
	,#{ifmmZipCode}
	,#{ifmmAddress}
	,#{ifmmDetailAddress}
	,#{ifmmExtraAddress}
	,13
	,0
	,#{ifmmEmailCheck}
	,now()
	,now()
	,0
)

	SELECT last_insert_id()

mapper.update:
UPDATE member
SET
	ifmmName = #{ifmmName}
	,ifmmId = #{ifmmId}
	,ifmmDob = #{ifmmDob}
	,ifmmGender = #{ifmmGender}
