	,ifmmRadioOperator = #{ifmmRadioOperator}
	,ifmmPhone = #{ifmmPhone}
	,ifmmZipCode = #{ifmmZipCode}
	,ifmmAddress = #{ifmmAddress}
	,ifmmDetailAddress = #{ifmmDetailAddress}
	,ifmmExtraAddress = #{ifmmExtraAddress}
	,modifiedAt = now()
where 1=1
	and ifmmSeq = #{ifmmSeq}

