mapper.sql:
FROM menu m
WHERE 1=1
AND ifmnDelNy = #{shDelNy}
AND ifmnCreatedAt between #{shDateStart} and #{shDateEnd}

	AND ifmnName LIKE CONCAT('%',#{shValue},'%')
	AND ifmnPrice = #{shValue}
	AND ifmnInfo LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO menu (
	ifmnName
	,ifstSeq
	,ifmnPrice
	,ifmnInfo
	,ifmnSet_div
	,ifmnDiscount
	,ifmnDelNy
	,ifmnCreatedAt
)
VALUES (
	#{ifmnName}
	,#{ifstSeq}
	,#{ifmnPrice}
	,#{ifmnInfo}
	,#{ifmnSet_div}
	,#{ifmnDiscount}
	,#{ifmnDelNy}
	,now()
)


	SELECT last_insert_id()

mapper.update:
 	UPDATE menu
 	SET
		ifmnName = #{ifmnName}
		,ifstSeq = #{ifstSeq}
		,ifmnPrice = #{ifmnPrice}
		,ifmnInfo = #{ifmnInfo}
		,ifmnSet_div = #{ifmnSet_div}
		,ifmnDiscount = #{ifmnDiscount}
		,ifmnDelNy = #{ifmnDelNy}
		,ifmnModifiedAt = now()

	WHERE 1=1
		and ifmnSeq = #{ifmnSeq}
