mapper.sql:
FROM menu m
WHERE 1=1
AND ifmnDelNy = #{shDelNy}
AND ifmnCreatedAt between #{shDateStart} and #{shDateEnd}

	AND ifmnName LIKE CONCAT('%',#{shValue},'%')
	AND ifmnPrice = #{shValue}
	AND ifmnInfo LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO menu (
	m.ifmnSeq
	,m.ifmnName
	,m.ifmnPrice
	,m.ifmnInfo
	,m.ifmnDiscount
	,m.ifmnSet_div
	,m.ifmnDelNy
)
VALUES (
	#{ifmnSeq}
	,#{ifmnName}
	,#{ifmnPrice}
	,#{ifmnInfo}
	,#{ifmnDiscount}
	,#{ifmnSet_div}
	,#{ifmnDelNy}
)


	SELECT last_insert_id()

mapper.update:
 	UPDATE menu
 	SET
		m.ifmnName = #{ifmnName}
		,m.ifmnPrice = #{ifmnPrice}
		,m.ifmnInfo = #{ifmnInfo}
		,m.ifmnSet_div = #{ifmnSet_div}

	WHERE
		m.ifmnSeq = #{m.ifmnSeq}
