mapper.sql:
 	FROM buy b
		JOIN store s on s.ifstSeq = b.ifstSeq
		JOIN member m on m.ifmmSeq = b.ifmmSeq
		JOIN menu mn on mn.ifmnSeq = b.ifmnSeq
	wHERE 1=1		
	AND b.ifbyDelNy = #{shDelNy}
	AND ifbyCreatedAt between #{shDateStart} and #{shDateEnd}


		AND s.ifstName = #{shValue}
		AND mn.ifmnName LIKE CONCAT('%', #{shValue},'%')
		AND m.ifmmName LIKE CONCAT('%', #{shValue},'%')


		AND b.ifbyCreatedAt BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.delete:
DELETE FROM buy
WHERE 1=1
	AND ifbySeq = #{ifbySeq}

mapper.insert:
insert INTO buy (
	ifstSeq
	,ifmnSeq
	,ifbyCount
	,ifbyPrice
	,ifbyPay
	,ifbyDelNy
	,ifbyCreatedAt
	,ifmmSeq
	,iforSeq
) VALUES (
	#{ifstSeq}
	,#{ifmnSeq}
	,#{ifbyCount}
	,#{ifbyPrice}
	,#{ifbyPay}
	,0
	,now()
	,#{ifmmSeq}
	,#{iforSeq}
)

		SELECT last_insert_id()

insert INTO orders (
	iforName
	,iforPrice
	,iforCount
	,iforCreatedAt
	,ifmmSeq
) VALUES (
	#{iforName}
	,#{iforPrice}
	,#{iforCount}
	,now()
	,#{ifmmSeq}
)

		SELECT last_insert_id()
