mapper.sql:
 	FROM buy b
		JOIN store s on s.ifstSeq = b.ifstSeq
		JOIN member m on m.ifmmSeq = b.ifmmSeq
		JOIN menu mn on mn.ifmnSeq = b.ifmnSeq
	wHERE 1=1		
	AND b.iforDelNy = #{shDelNy}
	AND iforCreatedAt between #{shDateStart} and #{shDateEnd}


		AND s.ifstName = #{shValue}
		AND mn.ifmnName LIKE CONCAT('%', #{shValue},'%')
		AND m.ifmmName LIKE CONCAT('%', #{shValue},'%')


		AND b.iforCreatedAt BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.delete:
DELETE FROM buy
WHERE 1=1
	AND iforSeq = #{iforSeq}

mapper.insert:
insert INTO buy (
	ifstSeq
	,ifmnSeq
	,iforCount
	,iforPrice
	,iforPay
	,iforDelNy
	,iforCreatedAt
	,ifmmSeq
) VALUES (
	#{ifstSeq}
	,#{ifmnSeq}
	,#{totalCount}
	,#{totalPrice}
	,#{iforPay}
	,0
	,now()
	,#{ifmmSeq}
)

		SELECT last_insert_id()
