mapper.sql:
 	FROM reservation r
		JOIN store s on s.ifstSeq = r.ifstSeq
		JOIN member m on m.ifmmSeq = r.ifmmSeq
		JOIN menu mn on mn.ifmnSeq = r.ifmnSeq
	wHERE 1=1		
	AND r.ifrvDelNy = #{shDelNy}



		AND s.ifstName = #{shValue}
		AND mn.ifmnName LIKE CONCAT('%', #{shValue},'%')
		AND m.ifmmName LIKE CONCAT('%', #{shValue},'%')



		AND r.ifrvCreatedAt BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.insert:
INSERT INTO reservation(
	ifrvDate
	,ifrvTime
	,ifrvCount
	,ifrvPerson
	,ifrvPrice
	,ifstSeq
	,ifmnSeq
	,ifmmSeq
)
VALUES (
	now()
	,now()
	,#{ifrvCount}
	,#{ifrvPerson}
	,#{ifrvPrice}
	,#{ifstSeq}
	,#{ifmnSeq}
	,#{ifmmSeq}
)

	SELECT last_insert_id()
