mapper.insert:
INSERT INTO store (
	ifstName 
	,ifstPhone 
	,ifstZipCode
	,ifstAddress
	,ifstAddressDetail
	,ifstAddressExtra
	,ifstInfo 
	,ifstDirections 
	,ifstOrderNy
	,ifstDelNy
	,ifstCreatedAt
	,ifstModifiedAt
)
VALUES (
	#{ifstName}
	,#{ifstPhone}
	,#{ifstZipCode}
	,#{ifstAddress}
	,#{ifstAddressDetail}
	,#{ifstAddressExtra}
	,#{ifstInfo}
	,#{ifstDirections}
	,#{ifstOrderNy}
	,0
	,now()
	,now()
)

	SELECT last_insert_id()

INSERT INTO runningTime (
	ifrtDay 
	,ifrtStartTime 
	,ifrtEndTime
	,ifrtOpening
	,ifstSeq
)
VALUES (
	#{ifrtDay}
	,#{ifrtStartTime}
	,#{ifrtEndTime}
	,#{ifrtOpening}
	,#{ifstSeq}
)

	SELECT last_insert_id()

mapper.update:
UPDATE store
SET
	ifstName = #{ifstName}
	,ifstPhone = #{ifstPhone}
	,ifstZipCode = #{ifstZipCode}
	,ifstAddress = #{ifstAddress}
	,ifstAddressDetail = #{ifstAddressDetail}
	,ifstAddressExtra = #{ifstAddressExtra}
	,ifstInfo = #{ifstInfo}
	,ifstDirections = #{ifstDirections}
	,ifstOrderNy = #{ifstOrderNy}
	,ifstModifiedAt = now()
where 1=1
	and ifstSeq = #{ifstSeq}

UPDATE runningTime
SET
	ifrtDay = #{ifrtDay}
	,ifrtStartTime = #{ifrtStartTime}
	,ifrtEndTime = #{ifrtEndTime}
	,ifrtOpening = #{ifrtOpening}
	,ifstSeq = #{ifstSeq}
where 1=1
	and ifstSeq = #{ifstSeq}
	and ifrtDay = #{ifrtDay}
