mapper.insert:
INSERT INTO store (
	ifstName 
	,ifstPhone 
	,ifstZipCode
	,ifstAddress
	,ifstAddressDetail
	,ifstAddressExtra
	,ifstLat
	,ifstLng
	,ifstInfo 
	,ifstDirections 
	,ifstOrderNy
	,ifstDelNy
	,ifstCreatedAt
	,ifstModifiedAt
)
VALUES (
	#{ifstName}
	,#{ifstPhone}
	,#{ifstZipCode}
	,#{ifstAddress}
	,#{ifstAddressDetail}
	,#{ifstAddressExtra}
	,#{ifstLat}
	,#{ifstLng}
	,#{ifstInfo}
	,#{ifstDirections}
	,#{ifstOrderNy}
	,0
	,now()
	,now()
)

	SELECT last_insert_id()

INSERT INTO runningTime (
	ifrtDay 
	,ifrtStartTime 
	,ifrtEndTime
	,ifrtOpening
	,ifstSeq
)
VALUES (
	#{ifrtDay}
	,#{ifrtStartTime}
	,#{ifrtEndTime}
	,#{ifrtOpening}
	,#{ifstSeq}
)

	SELECT last_insert_id()

mapper.update:
UPDATE store
SET
	ifstName = #{ifstName}
	,ifstPhone = #{ifstPhone}
	,ifstZipCode = #{ifstZipCode}
	,ifstAddress = #{ifstAddress}
	,ifstAddressDetail = #{ifstAddressDetail}
	,ifstAddressExtra = #{ifstAddressExtra}
	,ifstLat = #{ifstLat}
	,ifstLng = #{ifstLng}
	,ifstInfo = #{ifstInfo}
	,ifstDirections = #{ifstDirections}
	,ifstOrderNy = #{ifstOrderNy}
	,ifstModifiedAt = now()
where 1=1
	and ifstSeq = #{ifstSeq}

UPDATE runningTime
SET
	ifrtDay = #{ifrtDay}
	,ifrtStartTime = #{ifrtStartTime}
	,ifrtEndTime = #{ifrtEndTime}
	,ifrtOpening = #{ifrtOpening}
	,ifstSeq = #{ifstSeq}
where 1=1
	and ifstSeq = #{ifstSeq}
	and ifrtDay = #{ifrtDay}

mapper.insert:
INSERT INTO storeUploaded (
    type
    ,defaultNy
    ,sort
    ,originalName
    ,uuidName
    ,ext
    ,size
    ,delNy
    ,pseq
    ,path
   )
   VALUES (
	#{type}
	,#{defaultNy}
	,#{sort}
	,#{originalName}
	,#{uuidName}
	,#{ext}
	,#{size}
	,0
	,#{ifstSeq} 
	,#{path}
	)

	SELECT last_insert_id()

INSERT INTO favorite (
	ifstSeq 
	,iffvUseNy 
	,ifmmSeq
)
VALUES (
	#{ifstSeq}
	,#{iffvUseNy}
	,#{ifmmSeq}
)

	SELECT last_insert_id()

mapper.update:
UPDATE favorite
SET
	iffvSeq = #{iffvSeq}
	,iffvUseNy = #{iffvUseNy}
	,ifmmSeq = #{ifmmSeq}
	,ifstSeq = #{ifstSeq}
where 1=1
	and iffvSeq = #{iffvSeq}
